import React, { useState } from "react";

import logo from "./logo.svg";
import "./App.scss";
import "./scss/variables.scss";
import "./scss/style.scss";
import "./scss/frequently_used.scss";

import { ApolloClient, InMemoryCache, ApolloProvider } from "@apollo/client";
import { GithubPullRequests } from "./components/GithubPullRequests";
import { RepoInfo } from "./components/RepoInfo";
import { Statistic } from "./components/Statistic";

const client = new ApolloClient({
  uri: "http://localhost:3001/graphql",
  cache: new InMemoryCache(),
});

function App() {
  //* for test
  const repoId = 1;
  const fromYearMonth = 202301;
  const toYearMonth = 202312;
  const repoOwner = "Nekoiii";
  const repoName = "Rails_Tutorial";

  const [fromDateInput, setFromDateInput] = useState("");
  const [toDateInput, setToDateInput] = useState("");

  const handleDateInput = (e: React.ChangeEvent<HTMLInputElement>) => {
    console.log("e.target.value", e.target.value);
    setFromDateInput(e.target.value);
  };

  return (
    <ApolloProvider client={client}>
      <div>
        FromDate:
        <input
          type="text"
          value={fromDateInput}
          onChange={(e) => handleDateInput(e)}
        />
        ToDate:
        <input
          type="text"
          value={toDateInput}
          onChange={(e) => {
            setToDateInput(e.target.value);
          }}
        />
      </div>

      <RepoInfo repoId={repoId} />
      <Statistic
        repoId={repoId}
        fromYearMonth={fromYearMonth}
        toYearMonth={toYearMonth}
      />
      {/* <GithubPullRequests /> */}
    </ApolloProvider>
  );
}

export default App;
