import React, { useState, useEffect } from "react";
import moment from "moment";

import { useQuery, useLazyQuery, gql } from "@apollo/client";
import "../scss/components/statistic.scss";

interface Props {
  repoId: number;
}

// '!' after 'ID' means not null
const STATISTIC_QUERY = gql`
  query ($repositoryId: ID!, $fromYearMonth: String, $toYearMonth: String) {
    statistic(
      repositoryId: $repositoryId
      fromYearMonth: $fromYearMonth
      toYearMonth: $toYearMonth
    ) {
      year
      month
      userId
      isOverall
      totalMergeTime
      averageMergeTime
      mergedPrCount
    }
  }
`;

const seconds_to_formated_time = (seconds: number) => {
  const formated_time = new Date(seconds * 1000).toISOString().slice(11, 19);
  // console.log("seconds_to_formated_time--formated_time:", formated_time);
  return formated_time;
};

export const Statistic = ({ repoId }: Props) => {
  console.log("render Statistic----------");
  const [fromDateInput, setFromDateInput] = useState<string>("202301");
  const [toDateInput, setToDateInput] = useState<string>("202312");
  const handleDateInput = (e: React.ChangeEvent<HTMLInputElement>) => {
    setFromDateInput(e.target.value);
  };

  const [fetchDate, setFetchDate] = useState<{
    fetchFromDate: string;
    fetchToDate: string;
  }>({
    fetchFromDate: fromDateInput,
    fetchToDate: toDateInput,
  });

  const [fetchData, { data, loading, error }] = useLazyQuery(STATISTIC_QUERY, {
    // network-only: Fetches data directly from the network on every query, bypassing cache.
    // otherwise, it will not query the data the second time the button is clicked
    fetchPolicy: "network-only",
    variables: {
      repositoryId: repoId,
      fromYearMonth: fetchDate.fetchFromDate,
      toYearMonth: fetchDate.fetchToDate,
    },
    onCompleted: (data) => {
      console.log("Data fetched:", data);
    },
    onError: (error) => {
      console.log("Error fetching data:", error);
    },
  });

  // Fetch data when the button is clicked
  const handleFetchClick = () => {
    console.log("handleFetchClick");

    fetchData();
    setFetchDate({
      fetchFromDate: fromDateInput,
      fetchToDate: toDateInput,
    });
  };

  useEffect(() => {
    console.log("repoId---", repoId);
    fetchData();
  }, []);

  return (
    <div className="statistic_component">
      <h1>--Statistic of : {}---</h1>
      {loading && <div>Loading...</div>}
      {error && <div>Error: {error.message}</div>}
      <div>
        FromDate:
        <input
          type="text"
          value={fromDateInput}
          onChange={(e) => handleDateInput(e)}
        />
        ToDate:
        <input
          type="text"
          value={toDateInput}
          onChange={(e) => {
            setToDateInput(e.target.value);
          }}
        />
        <button className="button-grey" onClick={handleFetchClick}>
          Fetch Data
        </button>
      </div>
      {data && data.statistic && (
        <div className="stat_list">
          {data.statistic.map((stat: any, index: number) => (
            <div
              key={index}
              className={"stat_box ".concat(
                stat.isOverall ? "is_overall" : "not_overall"
              )}
            >
              <div className="stat_box_content">
                {stat.isOverall ? (
                  <div>Overall</div>
                ) : (
                  <div>User:{stat.userId}</div>
                )}
                <div>mergedPrCount: {stat.mergedPrCount}</div>
                <div>
                  totalMergeTime:{" "}
                  {seconds_to_formated_time(stat.totalMergeTime)}
                </div>
                <div>
                  averageMergeTime:{" "}
                  {seconds_to_formated_time(stat.averageMergeTime)}
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};
