import { useQuery, gql } from "@apollo/client";

import "../scss/components/repoInfo.scss";

// *unfinished: need to be refine here
const owner = "Nekoiii";
// const owner = "Nekoiii2";
const repoName = "Rails_Tutorial";

interface Props {
  repoId: number;
}

const REPO_INFO_QUERY = gql`
  query ($id: ID!) {
    repoInfoById(id: $id) {
      name
      description
      url
      githubCreatedAt
    }
  }
`;

export const RepoInfo = ({ repoId }: Props) => {
  const { data, loading, error } = useQuery(REPO_INFO_QUERY, {
    variables: { id: repoId },
  });

  console.log("RepoInfo--Data:", data);

  return (
    <div className="repo_info">
      {loading && <div>Loading...</div>}
      {error && <div>Error: {error.message}</div>}
      {data && data.repoInfoById && (
        <div>
          <div>--RepoInfo--xxxx</div>
          <div>
            Repository Name: {data.repoInfoById.name}
            Description: {data.repoInfoById.description}
            Created At: {data.repoInfoById.githubCreatedAt}
          </div>
        </div>
      )}
    </div>
  );
};
