import moment from "moment";

import { useQuery, gql } from "@apollo/client";
import "../scss/components/statistic.scss";

interface Props {
  repoId: number;
  fromYearMonth: number;
  toYearMonth: number;
}

// '!' after 'ID' means not null
const STATISTIC_QUERY = gql`
  query ($repoId: ID!, $fromYearMonth: Int, $toYearMonth: Int) {
    statistic(
      repoId: $repoId
      fromYearMonth: $fromYearMonth
      toYearMonth: $toYearMonth
    ) {
      id
      year
      month
      totalMergeTime
      averageMergeTime
    }
  }
`;

const seconds_to_formated_time = (seconds: number) => {
  const formated_time = new Date(seconds * 1000).toISOString().slice(11, 19);
  // console.log("seconds_to_formated_time--formated_time:", formated_time);
  return formated_time;
};

export const Statistic = ({ repoId, fromYearMonth, toYearMonth }: Props) => {
  // * unfinished, 'repoId: 1' for test
  const { data, loading, error } = useQuery(STATISTIC_QUERY, {
    variables: { repoId: 1, fromYearMonth: 5, toYearMonth: 10 },
  });

  console.log("Statistic--props:", repoId, fromYearMonth, toYearMonth);
  console.log("Statistic--Data:", data);

  return (
    <div className="statistic_component">
      --Statistic---
      {loading && <div>Loading...</div>}
      {error && <div>Error: {error.message}</div>}
      {data && data.statistic && (
        <div>
          xxx
          {data.statistic.map((stat: any) => (
            <div
              key={stat.id}
              className={"stat_box ".concat(
                stat.is_overall ? "is_overall" : "not_overall"
              )}
            >
              <div>id: {stat.id}</div>
              <div>stat.is_overall: {stat.is_overall}</div>
              {stat.is_overall && <div>User:{stat.user_id}</div>}
              <div>Year: {stat.year}</div>
              <div>month: {stat.month}</div>
              <div>
                totalMergeTime: {seconds_to_formated_time(stat.totalMergeTime)}
              </div>
              <div>
                averageMergeTime:{" "}
                {seconds_to_formated_time(stat.averageMergeTime)}
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};
